	:param doctype: DocType name as string."""
	from frappe.model.document import Document
	global _classes

	if not doctype in _classes:
		module_name, custom = frappe.db.get_value("DocType", doctype, ("module", "custom"), cache=True) \
			or ["Core", False]

		if custom:
